				<ng-icon hlm size="sm" [name]="computedIcon()" />
			</button>
		</div>
	`,
})
export class Input20Component {
	public readonly visibility = signal(false);
	public readonly computedIcon = computed<IconType>(() => (this.visibility() ? 'lucideEyeOff' : 'lucideEye'));
